mapper.delete:
delete from file_attachment
where id in (

        #{el.id}

)

mapper.select:
select id as id, file_name as fileName, path_url as pathUrl, file_type as fileType, file_status as fileStatus, suffix_name as suffixName from file_attachment t
and t.file_name like #{fileName}
or t.file_type = #{fileType}
or t.file_status = #{fileStatus}
